import Modal from "../../../components/UI/Modal";
import YouTube from "react-youtube";
import { useContext } from "react";
import DetailContext from "./store/detail-context";

const Detail = (props) => {
  const detailCtx = useContext(DetailContext);

  console.log(detailCtx.movieTrailer);
  console.log(detailCtx.movieTeaser);
  return (
    <Modal onClick={props.onClick}>
      <div className="d-flex justify-content-end">
        <div>
          <button onClick={props.onClick}>X</button>
        </div>
      </div>
      <div className="container-fluid">
        <div className="row">
          <div className="col-5">
            <div className="d-flex flex-column bd-highlight mb-3 ">
              <div className="p-2 bd-highlight text-light ">
                <h3 className="fw-bold text-light"> {props.name} </h3>
                <h1 className=" mt-4 bg-light border-2 border-top border-light" />
              </div>
              <div className="ps-2 bd-highlight text-light">
                <p>
                  Release Date : 2022-06-01
                  <span>{props.vote}</span>
                </p>
              </div>
              <div className="ps-2 bd-highlight text-light">
                {props.overview}
              </div>
            </div>
          </div>
          <div className="col-7">
            <div>
              {
                (detailCtx.movieTrailer.length = 0 ? (
                  ""
                ) : (
                  <iframe
                    width="848px"
                    height="480px"
                    src="https://www.youtube.com/embed/M30iVU51U6c"
                  ></iframe>
                ))
              }
            </div>
          </div>
        </div>
      </div>
    </Modal>
  );
};
export default Detail;
