import React from "react";
import Detail from "./Detail";
import { Fragment, useState, useEffect } from "react";
import { useContext } from "react";
import DetailContext from "./store/detail-context";

const MovieDetail = (props) => {
  const [youtube, setYoutube] = useState([]);
  const [trailerState, setTrailerState] = useState([]);
  const [teaserState, setTeaserState] = useState([]);

  const detailCtx = useContext(DetailContext);
  console.log(detailCtx);
  const dataList = detailCtx.data.flat();
  const abc = dataList.filter(
    (img) =>
      `https://image.tmdb.org/t/p/original${img.poster_path}` ===
      detailCtx.items.toString()
  );
  console.log(abc[0].id);
  useEffect(() => {
    const fetchMeals = async (type) => {
      const response = await fetch(
        `https://api.themoviedb.org/3//movie/${type}/videos?api_key=a4ae14e6dc40d4017e61df4a11712f48`

        // "https://api.themoviedb.org/3/movie/550?api_key=a4ae14e6dc40d4017e61df4a11712f48"
        // "https://api.themoviedb.org/3/trending/all/week?api_key=a4ae14e6dc40d4017e61df4a11712f48&language=en-US"
      );
      const responseData = await response.json();
      console.log(responseData.results);
      setYoutube(responseData.results);
    };
    fetchMeals(abc[0].id);
  }, []);

  const trailerFilter = youtube.filter((el) => el.type === "Trailer");
  setTrailerState(trailerFilter);
  console.log(trailerFilter);
  const teaserFilter = youtube.filter((el) => el.type === "Teaser");
  //   const trailerFilter = youtube.filter((el) => el.type === "Trailer");
  //   setTrailerState(trailerFilter);
  //   const teaserFilter = youtube.filter((el) => el.type === "Teaser");
  //   setTeaserState(teaserFilter);
  //   console.log(teaserState);

  return (
    <Fragment>
      <Detail onClick={props.onClick} />
    </Fragment>
  );
};
export default MovieDetail;
