import NavIcon from "../browse/NavIcon";
import classes from "./SearchForm.module.css";
import DetailContext from "../browse/Menu/store/detail-context";
import { useContext, useState, useEffect } from "react";
const SearchForm = (props) => {
  const detailCtx = useContext(DetailContext);
  const [youtube, setYoutube] = useState([]);
  const [key, setKey] = useState("");
  const dataList = detailCtx.data.flat();
  const dataMap = dataList.map((el) => {
    return {
      poster_path: el.poster_path,
      name: el.name,
      title: el.title,
    };
  });
  console.log(dataMap);
  console.log(dataList);
  const abc = dataMap.filter((el) => el.name.includes("cabin"));
  console.log(abc);
  //   function filterByValue(array, string) {
  //     return array.filter((o) =>
  //       Object.keys(o).some((k) =>
  //         o[k].toLowerCase().includes(string.toLowerCase())
  //       )
  //     );
  //   }

  //   console.log(filterByValue(dataMap, "Cabin")); // [{name: 'Lea', country: 'Italy'}]
  //   //   console.log(filterByValue(arrayOfObject, "ita")); // [{name: 'Lea', country: 'Italy'}, {name: 'John', country: 'Italy'}]

  return (
    <>
      <div className="container">
        <div className={classes.center}>
          <input type="text" />
          <span>
            <svg
              width="25"
              className="svg-inline--fa fa-search fa-w-16"
              fill="#ccc"
              aria-hidden="true"
              data-prefix="fas"
              data-icon="search"
              role="img"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 512 512"
            >
              <path d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z"></path>
            </svg>
          </span>
          <div className="d-flex flex-row-reverse bd-highlight mt-2 me-5">
            <button className="btn me-3 fw-bold text-white bg-primary">
              SEARCH
            </button>
            <button className="btn me-3 fw-bold">RESET</button>
          </div>
        </div>
      </div>
    </>
  );
};
export default SearchForm;
