import React from "react";
import { useState } from "react";
import NavBar from "./NavBar";
import Banner from "./Banner";
import MovieDetail from "./Menu/MovieDetail";
import Original from "./Menu/Original";
import Trending from "./Menu/Trending";
import TopRate from "./Menu/TopRate";
import ActionMovies from "./Menu/ActionMovies";
import ComedyMovie from "./Menu/ComedyMovie";
import HorrorMovies from "./Menu/HorrorMovies";
import RomanceMovies from "./Menu/RomanceMovies";
import Documentaries from "./Menu/Documentaries";
import Card from "../../components/UI/Card";
import { useContext, useEffect } from "react";
import DetailContext from "./Menu/store/detail-context";
function Browse() {
  const [showDetails, setShowDetails] = useState(false);
  const detailCtx = useContext(DetailContext);
  const [youtube, setYoutube] = useState([]);
  const showDetailHandler = () => {
    const dataList = detailCtx.data.flat();
    const abc = dataList.filter(
      (img) =>
        `https://image.tmdb.org/t/p/original${img.poster_path}` ===
        detailCtx.items.toString()
    );
    console.log(abc[0].id);

    const fetchMeals = async (type) => {
      const response = await fetch(
        `https://api.themoviedb.org/3//movie/${type}/videos?api_key=a4ae14e6dc40d4017e61df4a11712f48`
      );
      const responseData = await response.json();
      console.log(responseData.results);
      setYoutube(responseData.results);
      const trailerFilter = responseData.results.filter(
        (el) => el.type === "Trailer"
      );
      detailCtx.movieTrailer.push(trailerFilter);
      const teaserFilter = responseData.results.filter(
        (el) => el.type === "Teaser"
      );
      detailCtx.movieTeaser.push(teaserFilter);
    };
    fetchMeals(abc[0].id);
    console.log(abc);
    setShowDetails(true);
  };
  const hideDetail = () => {
    detailCtx.items.shift();
    setShowDetails(false);
  };
  const getDetailHandler = (props) => {};
  return (
    <div>
      <NavBar />
      <Banner />
      <Card>
        <Original onDetail={getDetailHandler} onClick={showDetailHandler} />
        <Trending onDetail={getDetailHandler} onClick={showDetailHandler} />
        <TopRate onDetail={getDetailHandler} onClick={showDetailHandler} />
        <ActionMovies onDetail={getDetailHandler} onClick={showDetailHandler} />
        <ComedyMovie onDetail={getDetailHandler} onClick={showDetailHandler} />
        <HorrorMovies onDetail={getDetailHandler} onClick={showDetailHandler} />
        <RomanceMovies
          onDetail={getDetailHandler}
          onClick={showDetailHandler}
        />
        <Documentaries
          onDetail={getDetailHandler}
          onClick={showDetailHandler}
        />
      </Card>
      {showDetails ? <MovieDetail onClick={hideDetail} /> : ""}
    </div>
  );
}

export default Browse;
