    movieTrailer: [
      {
        key: key,
      },
    ],
    movieTeaser: [],
  };
  return (
    <DetailContext.Provider value={defaultContext}>
      {props.children}
    </DetailContext.Provider>
  );
};
export default DetailProvider;
