import React from "react";
import Card from "../../../components/UI/Card";
import Original from "./Original";
import Trending from "./Trending";
import TopRate from "./TopRate";
import ActionMovies from "./ActionMovies";
import ComedyMovie from "./ComedyMovie";
import HorrorMovies from "./HorrorMovies";
import RomanceMovies from "./RomanceMovies";
import Documentaries from "./Documentaries";
import Detail from "./Detail";
import Modal from "../../../components/UI/Modal";
import { Fragment, useState } from "react";

const MovieDetail = (props) => {
  const [showDetail, setShowDetail] = useState(false);
  const [dataList, setDataList] = useState([]);
  const detailHandler = (props) => {
    console.log(props);
    dataList(...props);
  };
  const showDetailHandler = () => {
    setShowDetail(true);
  };
  console.log(dataList);
  const RenderList = (
    <Card>
      <Original onDetail={detailHandler} onClick={showDetailHandler} />
      <Trending onDetail={detailHandler} onClick={showDetailHandler} />
      <TopRate onDetail={detailHandler} onClick={showDetailHandler} />
      <ActionMovies onDetail={detailHandler} onClick={showDetailHandler} />
      <ComedyMovie onDetail={detailHandler} onClick={showDetailHandler} />
      <HorrorMovies onDetail={detailHandler} onClick={showDetailHandler} />
      <RomanceMovies onDetail={detailHandler} onClick={showDetailHandler} />
      <Documentaries onDetail={detailHandler} onClick={showDetailHandler} />
    </Card>
  );

  const hideHandler = () => {
    setShowDetail(false);
  };
  return (
    <Fragment>
      {showDetail ? <Detail onClick={hideHandler} /> : ""}

      {RenderList}
    </Fragment>
  );
};
export default MovieDetail;
